mapper.select:
        select IFNULL(sum(od.pay_num),0)
        from eb_order o
        left join eb_order_detail od on o.order_no = od.order_no
        where o.is_del = 0
--         and o.refund_status < 3
        and o.cancel_status = 0
        and o.status < 9
        and o.uid = #{uid}
        and o.type = #{marketingType}
        and od.product_id = #{proId}
