mapper.select:
SELECT p.id,p.image,p.name as name,p.keyword,p.category_id as categoryId,p.price,p.sales,p.stock,p.ficti, p.audit_status as auditStatus,p.reason,p.rank,m.name as merchantName,m.is_self as isSelf,p.rank,p.type, p.spec_type as specType FROM eb_product AS p right join eb_merchant AS m on p.mer_id = m.id where p.is_del = 0 and p.is_recycle = 0 and p.marketing_type = 0
and p.is_show = 1 and p.audit_status in (0,2)
and p.is_show = 0 and p.is_audit = 0 and p.audit_status in (0,2)
and p.audit_status = 1 and p.is_audit = 1
and p.audit_status = 3 and p.is_audit = 0
and p.mer_id = #{merId}
and m.is_self = #{self}
and find_in_set(p.category_id, #{categoryIds, jdbcType=VARCHAR})
and (p.name like CONCAT('%', #{keywords, jdbcType=VARCHAR}, '%') or p.keyword like CONCAT('%', #{keywords, jdbcType=VARCHAR}, '%'))
and p.is_paid_member = #{isPaidMember}
and p.type = #{productType}
ORDER BY p.rank desc, p.id desc

SELECT COUNT(*) FROM eb_product AS p right join eb_merchant AS m on p.mer_id = m.id where p.is_del = 0 and p.is_recycle = 0 and p.marketing_type = 0
and p.is_show = 1 and p.audit_status in (0,2)
and p.is_show = 0 and p.is_audit = 0 and p.audit_status in (0,2)
and p.audit_status = 1 and p.is_audit = 1
and p.audit_status = 3 and p.is_audit = 0
and p.mer_id = #{merId}
and m.is_self = #{self}
and find_in_set(p.category_id, #{categoryIds, jdbcType=VARCHAR})
and (p.name like CONCAT('%', #{keywords, jdbcType=VARCHAR}, '%') or p.keyword like CONCAT('%', #{keywords, jdbcType=VARCHAR}, '%'))
and p.is_paid_member = #{isPaidMember}
and p.type = #{productType}

SELECT p.id,p.image,p.name as name,p.price,p.ot_price as otPrice,p.price,p.sales,p.stock,p.ficti, p.unit_name as unitName,p.reason,p.mer_id as merId,m.name as merName,m.category_id as merCategoryId,m.type_id as merTypeId, p.category_id as categoryId,p.brand_id as brandId,p.vip_price as vipPrice,p.is_paid_member as isPaidMember FROM eb_product AS p right join eb_merchant AS m on p.mer_id = m.id where p.is_del = 0 and p.is_recycle = 0 and p.is_show = 1 and p.audit_status in (0,2) and p.marketing_type = 0 and p.type <> 1
and find_in_set(p.id, #{id})
and find_in_set(p.category_id, #{categoryId})
and find_in_set(p.brand_id, #{brandId})
and p.price >= #{minPrice}
and p.price <= #{maxPrice}
and (p.name like CONCAT('%', #{keywords, jdbcType=VARCHAR}, '%') or p.keyword like CONCAT('%', #{keywords, jdbcType=VARCHAR}, '%'))
and m.is_self = #{isSelf}
and find_in_set(p.mer_id, #{merId})
${lastStr}

SELECT p.id,p.image,p.name,p.price,p.stock,p.is_show as isShow,p.spec_type as specType, m.name as merName,m.star_level as merStarLevel,p.brand_id as brandId, pc.name as categoryName, p.category_id as categoryId, (p.sales + p.ficti) as sales,p.unit_name as unitName,p.type FROM eb_product AS p left join eb_merchant AS m on p.mer_id = m.id left join eb_product_category AS pc on p.category_id = pc.id where p.is_del = 0 and p.is_recycle = 0 and p.audit_status in (0,2) and p.type in (0,2,5,6) and p.marketing_type = 0
and find_in_set(p.category_id, #{categoryId})
and p.is_show = #{isShow}
and p.name like CONCAT('%', #{name, jdbcType=VARCHAR}, '%')
and find_in_set(p.mer_id, #{merIds})
and m.star_level >= #{merStars}
and find_in_set(p.brand_id, #{brandId})
${lastStr}

SELECT p.id,p.image,p.name,p.price,p.stock,p.is_show as isShow,p.spec_type as specType,p.type,
       p.marketing_type as marketingType,(p.sales + p.ficti) as sales,p.unit_name as unitName,
       m.name as merName,m.star_level as merStarLevel,
       pc.name as categoryName
FROM eb_product AS p
left join eb_merchant AS m on p.mer_id = m.id
left join eb_product_category AS pc on p.category_id = pc.id
where p.is_del = 0 and p.is_recycle = 0
and p.audit_status in (0,2)
and p.type in (0,2,5,6) and p.marketing_type = 0 and p.mer_id = #{merId}

    and p.category_id = #{categoryId}


    and ${cateIdSql}


    and p.is_show = #{isShow}


    and p.name like CONCAT('%', #{name, jdbcType=VARCHAR}, '%')


    and p.id = #{productId}


    and p.category_id in (SELECT id FROM `eb_product_category` where `level` = 3 and is_del = 0 and pid in (SELECT id FROM `eb_product_category` WHERE `level` = 2 and is_del = 0 and FIND_IN_SET(pid, #{proCateIds})))

${lastStr}

SELECT p.id,p.image,p.name,p.is_show as isShow,p.category_id as categoryId,p.type, p.price,(p.sales + p.ficti) as sales,p.unit_name,p.stock, pc.name as categoryName FROM eb_product AS p left join eb_merchant AS m on p.mer_id = m.id left join eb_product_category AS pc on p.category_id = pc.id where p.is_del = 0 and p.is_recycle = 0 and p.audit_status in (0,2) and p.type in (0,2,5,6) and p.marketing_type = 0
and p.category_id = #{categoryId}
and find_in_set(#{cateId}, p.cate_id)
and p.is_show = #{isShow}
and p.name like CONCAT('%', #{name, jdbcType=VARCHAR}, '%')
and p.mer_id = #{merId}
and p.id = #{productId}
and p.category_id in (SELECT id FROM `eb_product_category` where `level` = 3 and is_del = 0 and pid in (SELECT id FROM `eb_product_category` WHERE `level` = 2 and is_del = 0 and FIND_IN_SET(pid, #{proCateIds})))
